Console.WriteLine(new List<(IEnumerable<int>, IEnumerable<int>)>(){File.ReadAllLines("input.txt").Select(j => j.Split(" ").ToList().Aggregate(new List<int>() as IEnumerable<int>, (acc, str) => int.TryParse(str, out int res) ? acc.Append(res) : acc)).Aggregate((new List<int>() as IEnumerable<int>, new List<int>() as IEnumerable<int>), (acc, i) => (acc.Item1.Append(i.First()).OrderBy(x => x), acc.Item2.Append(i.Last()).OrderBy(x => x)))}.Select(j => (j.Item1.ToList(), j.Item2.ToList())).Select(j => j.Item1.Select((i, ind) => Math.Abs(i - j.Item2[ind]))).First().Sum());