Console.WriteLine(new List<(IEnumerable<int>, IEnumerable<int>)>(){File.ReadAllLines("input.txt").Select(j => j.Split(" ").ToList().Aggregate(new List<int>() as IEnumerable<int>, (acc, str) => int.TryParse(str, out int res) ? acc.Append(res) : acc)).Aggregate((new List<int>() as IEnumerable<int>, new List<int>() as IEnumerable<int>), (acc, k) => (acc.Item1.Append(k.First()), acc.Item2.Append(k.Last())))}.Select(j => j.Item1.Select(i => (i, j.Item2.Count(x => x == i)))).First().Sum(x => x.i * x.Item2));